function validateEmailAddress(emailStr){
    // JavaScriptの型強制を使用して、emailStrを文字列に保証する
    emailStr = emailStr + '';

    // 正規表現で許可された構文に対してemailStrを比較する
    // emailStrが許可された構文であればtrueを返し、そうでなければfalseを返す
    if(emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
      return true;
    }
    else {
     return false;
    }
}
